mapper.select:
SELECT to_char(INPUT_DATE, 'mm') as month, sum(NUM) as num FROM (SELECT cs.NUM, cs.INPUT_DATE, w.TYPE FROM CONSUMPTION_STATISTICS cs, WL w WHERE cs.ID = w.ID and w.TYPE = #{type}
and INPUT_DATE >= to_date( to_char(#{startDate},'yyyy-mm-dd'),'yyyy-mm-dd')
and INPUT_DATE < to_date(to_char(#{endDate},'yyyy-mm-dd'),'yyyy-mm-dd')
) GROUP BY to_char(INPUT_DATE, 'mm') ORDER BY to_char(INPUT_DATE, 'mm')

SELECT to_char(INPUT_DATE, 'mm') AS month, sum(NUM * PRICE) AS num FROM ( SELECT cs.NUM, cs.INPUT_DATE, w.PRICE FROM CONSUMPTION_STATISTICS cs, WL w WHERE cs.ID = w.ID AND w.TYPE = #{type}
and cs.INPUT_DATE >= to_date( to_char(#{startDate},'yyyy-mm-dd'),'yyyy-mm-dd')
and cs.INPUT_DATE < to_date(to_char(#{endDate},'yyyy-mm-dd'),'yyyy-mm-dd')
) GROUP BY to_char(INPUT_DATE, 'mm')
